import React, { useState } from 'react'
import axios from 'axios'

const CreateUser = ({ token, currentUser }) => {
  const [newUser, setNewUser] = useState({ username: '', password: '' })

  const handleChange = (e) => {
    const { name, value } = e.target
    setNewUser({ ...newUser, [name]: value })
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    try {
      await axios.post(
        'http://localhost:5000/api/usuarios/create',
        newUser,
        { headers: { Authorization: `Bearer ${token}` } }
      )
      alert('Usuario creado exitosamente')
      setNewUser({ username: '', password: '' })
    } catch (err) {
      alert('Error al crear usuario')
    }
  }

  if (currentUser !== 'GustavoPeralta') {
    return <p className="p-4 text-red-600">Solo el administrador puede crear usuarios</p>
  }

  return (
    <form onSubmit={handleSubmit} className="p-4 space-y-2">
      <input type="text" name="username" placeholder="Nombre de usuario" value={newUser.username} onChange={handleChange} required />
      <input type="password" name="password" placeholder="ContraseÃ±a" value={newUser.password} onChange={handleChange} required />
      <button type="submit" className="bg-green-600 text-white px-4 py-2 rounded">Crear Usuario</button>
    </form>
  )
}

export default CreateUser
