import React, { useState } from 'react'
import axios from 'axios'

const Login = ({ setToken, setUsername }) => {
  const [form, setForm] = useState({ username: '', password: '' })

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value })
  }

  const handleSubmit = async e => {
    e.preventDefault()
    try {
      const res = await axios.post('http://localhost:5000/api/usuarios/login', form)
      const token = res.data.token
      const payload = JSON.parse(atob(token.split('.')[1]))
      setToken(token)
      setUsername(payload.username)
    } catch {
      alert('Credenciales incorrectas')
    }
  }

  return (
    <form onSubmit={handleSubmit} className="space-y-2">
      <input name="username" onChange={handleChange} placeholder="Usuario" className="border p-1 w-full" />
      <input name="password" type="password" onChange={handleChange} placeholder="Contraseña" className="border p-1 w-full" />
      <button className="bg-blue-500 text-white px-3 py-1 rounded">Iniciar sesión</button>
    </form>
  )
}

export default Login
