// frontend/src/components/Login.js
import React, { useState } from 'react'
import axios from 'axios'

const Login = ({ onLogin, onShowRegister }) => {
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')

  const handleSubmit = async (e) => {
    e.preventDefault()
    try {
      const res = await axios.post('http://localhost:5000/api/users/login', { username, password })
      onLogin(res.data.token, res.data.role, username)
    } catch (err) {
      alert('Credenciales inv치lidas')
    }
  }

  return (
    <div className="flex justify-center items-center h-screen bg-gray-100">
      <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow-md w-80 space-y-4">
        <h2 className="text-2xl font-bold text-center">Iniciar Sesi칩n</h2>
        <input
          type="text"
          placeholder="Usuario"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          className="border p-2 w-full"
        />
        <input
          type="password"
          placeholder="Contrase침a"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="border p-2 w-full"
        />
        <button type="submit" className="bg-blue-500 text-white p-2 rounded w-full">
          Ingresar
        </button>
        <button
          type="button"
          className="text-blue-600 underline w-full"
          onClick={onShowRegister}
        >
          Registrarse
        </button>
      </form>
    </div>
  )
}

export default Login
