import React, { useState } from 'react'
import { BrowserRouter } from 'react-router-dom'
import Login from './components/Login'
import RegisterForm from './components/RegisterForm'
import PoliceTabs from './components/PoliceTabs'

function App() {
  const [token, setToken] = useState(null)
  const [isRegistering, setIsRegistering] = useState(false)
  const [username, setUsername] = useState('')

  const handleLogin = (newToken, role, name) => {
    localStorage.setItem('token', newToken)
    setToken(newToken)
    setUsername(name)
  }

  const handleLogout = () => {
    localStorage.removeItem('token')
    setToken(null)
    setUsername('')
  }

  return (
    <BrowserRouter>
      {!token ? (
        isRegistering ? (
          <RegisterForm onBack={() => setIsRegistering(false)} />
        ) : (
          <Login onLogin={handleLogin} onShowRegister={() => setIsRegistering(true)} />
        )
      ) : (
        <PoliceTabs username={username} onLogout={handleLogout} />
      )}
    </BrowserRouter>
  )
}

export default App
